import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { LineChart, Line, BarChart, Bar, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import twitterResponse from '/Users/dharshanm/Desktop/project /client/src/performance_predictions.json'
import instagramResponse from '/Users/dharshanm/Desktop/project /client/src/segmentation_results.json'
import spotifyResponse from '/Users/dharshanm/Desktop/project /client/src/churn_predictions.json'
const Dashboard = () => 
    {


  return (
    <div>
      <h1>Social Media Analytics Dashboard</h1>

      {/* Twitter Performance Prediction */}
      <h2>Twitter Performance Prediction</h2>
      <ResponsiveContainer width="100%" height={300}>

      </ResponsiveContainer>

      {/* Twitter Metrics */}
      <h2>Twitter Metrics</h2>
      <ResponsiveContainer width="100%" height={300}>

      </ResponsiveContainer>

      {/* Instagram User Segmentation */}
      <h2>Instagram User Segmentation</h2>
      <ResponsiveContainer width="100%" height={300}>

      </ResponsiveContainer>

      {/* Instagram Metrics */}
      <h2>Instagram Metrics</h2>
      <ResponsiveContainer width="100%" height={300}>

      </ResponsiveContainer>

      {/* Spotify Churn Prediction */}
      <h2>Spotify Churn Prediction</h2>
      <ResponsiveContainer width="100%" height={300}>
        <PieChart>
          <Pie data={spotifyResponse} dataKey="churn_risk" nameKey="release_date" cx="50%" cy="50%" outerRadius={100}>
            {spotifyResponse.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={entry.churn_risk === 'High' ? '#ff8042' : '#0088fe'} />
            ))}
          </Pie>
          <Tooltip />
        </PieChart>
      </ResponsiveContainer>

      {/* Spotify Metrics */}
      <h2>Spotify Metrics</h2>
      <ResponsiveContainer width="100%" height={300}>
        <BarChart data={spotifyResponse}>
          <XAxis dataKey="release_date" />
          <YAxis />
          <CartesianGrid stroke="#eee" strokeDasharray="5 5"/>
          <Tooltip />
          <Legend />
          <Bar dataKey="popularity" fill="#8884d8" />
          <Bar dataKey="streams" fill="#82ca9d" />
          <Bar dataKey="reach" fill="#ffc658" />
          <Bar dataKey="active_time" fill="#ff8042" />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
};

export default Dashboard;
